import React, { Component } from "react";
import { v4 as uuidv4 } from "uuid";
import ContactForm from "./Components/ContactForm.jsx";
import Filter from "./Components/Filter";

export default class App extends Component {
  state = {
    contacts: [
      { id: "id-1", name: "Rosie Simpson", number: "459-12-56" },
      { id: "id-2", name: "Hermione Kline", number: "443-89-12" },
      { id: "id-3", name: "Eden Clements", number: "645-17-79" },
      { id: "id-4", name: "Annie Copeland", number: "227-91-26" },
    ],
    filter: "",
  };

  handleFilter = (e) => {
    this.setState({ filter: e.target.value });
  };

  showFilteredContacts = () => {
    const { contacts, filter } = this.state;
    return contacts.map((contact) => {
      contact.name.toLowerCase().includes(filter.toLowerCase());
    });
  };

  addContact = (name, number) => {
    let contactCard = { name: name, number: number, id: uuidv4() };

    this.setState((prevState) => {
      return { contacts: [...prevState.contacts, contactCard] };
    });
  };

  render() {
    const { contacts, filter } = this.state;
    console.log(this.showFilteredContacts());
    return (
      <>
        <h2>Phonebook</h2>
        <ContactForm addContact={this.addContact} />
        <h2>Contacts</h2>
        <Filter handleFilter={this.handleFilter} filter={filter} />
        <ul>
          {this.showFilteredContacts().map(({ name, number, id }) => (
            <li key={id} className="name">
              {name + `: ` + number}
            </li>
          ))}
        </ul>
      </>
    );
  }
}
