import React from 'react';
import style from '../ModuleStyles/PhoneBook.module.css';
export default function ContactItem({ name, number, id, onRemove }) {
  return (
    <li>
      <span className={(style.contactName, style.contactDetails)}>{name + ':'}</span>
      <span className={(style.contactPhone, style.contactDetails)}>{number}</span>
      <button className={style.removeButton} type="button" onClick={() => onRemove(id)}>
        Delete
      </button>
    </li>
  );
}
