import React, { Component } from "react";
import { v4 as uuidv4 } from "uuid";
import ContactForm from "./Components/ContactForm.jsx";
import Filter from "./Components/Filter";

export default class App extends Component {
  state = {
    contacts: [],
    filter: "",
  };

  handleFilter = (e) => {
    this.setState({ filter: e.target.value });
  };
  handleFilterVision = () => {
    const { contacts, filter } = this.state;
    return contacts.filter((contact) =>
      contact.name.toLowerCase().includes(filter.toLowerCase())
    );
  };
  addContact = (name, number) => {
    let contactCard = { nam: name, number: number, id: uuidv4() };
    console.log(contactCard);
    this.setState((prevState) => {
      return { contacts: [...prevState.contacts, contactCard] };
    });
  };

  render() {
    const { filter } = this.state;
    return (
      <>
        <h2>Phonebook</h2>
        <ContactForm addContact={this.addContact} />
        <h2>Contacts</h2>
        <Filter handleFilter={this.handleFilter} filter={filter} />
        <ul>
          {/* {this.handleFilterVision().map(({ name, number, id }) => (
            <li key={id} className="name">
              {name + `: ` + number}
            </li>
          ))} */}
        </ul>
      </>
    );
  }
}
